import React, { useState, useEffect } from 'react';
import { ShoppingCart, Youtube, Instagram } from 'lucide-react';
import { Link } from 'react-router-dom';

const Header = () => {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [hoveredNav, setHoveredNav] = useState(null);

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const navItems = [
    {
      title: 'PROJECT',
      description: 'PROJECT 카테고리는 졸업전시회의 메인 페이지로, 팀 선택 화면을 통해 각 팀별 주제를 탐색할 수 있습니다.',
      subItems: ['MAIN THEME', 'LOOK BOOK', 'RUNWAY', 'DESIGNER']
    },
    {
      title: 'STORE',
      description: 'STORE 카테고리는 졸업전시회와 관련된 다양한 상품을 소개하는 공간으로, 관람객이 전시회의 특별한 기념품을 손쉽게 구매할 수 있도록 돕습니다.',
      subItems: ['전체 굿즈', '팀별 굿즈']
    },
    {
      title: 'SHOW INFO',
      description: 'SHOW INFO 카테고리는 졸업전시회의 전반적인 정보를 제공하는 중요한 공간입니다.',
      subItems: ['전시정보', '관람정보', '오시는 길']
    },
    {
      title: 'BEHIND',
      description: 'BEHIND 카테고리는 졸업전시회의 뒷이야기를 담아 관람객에게 새로운 시각을 제공합니다.',
      subItems: ['SHOW BEHIND', 'BROCHURE BEHIND', 'MAKING BEHIND']
    },
    {
      title: 'ARCHIVE',
      description: '과거의 전시 기록들을 보관하고 있습니다.',
      subItems: ['2024', '2023', '2022']
    }
  ];

  return (
    <>
      <header className="w-full bg-black relative">
        {/* Top Bar */}
        <div className="w-full px-4">
          <div className="flex justify-between items-center h-12">
            {/* Left side with social icons and title */}
            <div className="flex flex-col items-start">
              <h1 className="text-white text-sm font-normal mb-2">
                2025 KUAD GRADUATION FASHION SHOW
              </h1>
              <div className="flex gap-4">
                <a href="https://youtube.com" target="_blank" rel="noopener noreferrer">
                  <Youtube className="w-5 h-5 text-white" />
                </a>
                <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
                  <Instagram className="w-5 h-5 text-white" />
                </a>
              </div>
            </div>

            {/* Cart Icon */}
            <div>
              <button className="relative">
                <ShoppingCart className="w-5 h-5 text-white" />
              </button>
            </div>
          </div>

          {/* Logo */}
          <div className="text-center py-4">
            <Link to="/" className="text-5xl font-bold text-white">
              SOMA
            </Link>
          </div>
        </div>

        {/* Navigation */}
        <nav className="border-t border-b border-gray-800">
          <div className="w-full">
            <ul className="flex justify-center gap-20 py-4">
              {navItems.map((item) => (
                <li
                  key={item.title}
                  className="relative group"
                  onMouseEnter={() => setHoveredNav(item.title)}
                  onMouseLeave={() => setHoveredNav(null)}
                >
                  <Link 
                    to={`/${item.title.toLowerCase()}`} 
                    className="text-white text-base font-medium hover:text-gray-300 relative"
                  >
                    {item.title}
                    <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-white transition-all duration-200 group-hover:w-full"></span>
                  </Link>
                </li>
              ))}
            </ul>
          </div>
        </nav>
      </header>

      {/* Dropdown Menu - Separated from navigation */}
      {hoveredNav && (
        <div className="w-full bg-white border-t border-gray-200">
          <div className="max-w-screen-2xl mx-auto py-8 px-4">
            <div className="flex justify-between">
              {/* Left section - Title and Description */}
              <div className="w-1/2">
                <h3 className="text-xl font-bold text-black mb-4">{hoveredNav}</h3>
                <p className="text-gray-600 text-sm">
                  {navItems.find(item => item.title === hoveredNav)?.description}
                </p>
              </div>
              
              {/* Right section - Sub Items */}
              <div className="w-1/4">
                <ul className="space-y-4">
                  {navItems.find(item => item.title === hoveredNav)?.subItems.map((subItem) => (
                    <li key={subItem}>
                      <Link 
                        to={`/${hoveredNav.toLowerCase()}/${subItem.toLowerCase()}`} 
                        className="text-black text-base hover:text-gray-600"
                      >
                        {subItem}
                      </Link>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Header;
